mapper.select:
select * from javaee.goods

     and name like concat('%', #{searchName}, '%') 
     and category = #{searchCategory}

mapper.update:
update goods
name = #{name}, category = #{category}, origin = #{origin}, price = #{price}, production_date = #{productionDate}, manufacturer = #{manufacturer},
where id = #{id}

